import * as React from "react";
import { useForm } from "react-hook-form";
import axios from 'axios';
import Headers from "./Header";
import "./styles.css";
import { first } from "lodash";



let renderCount = 0;

type FormValues = {
  date: string;
  tradeid: string;
  clientid: string;
};

interface dateResults {
  client: string;

}

// type RowValues = Record<string, number>;

// type Raw = RowValues & { month: number };

// type Formatted = Record<number, RowValues | undefined>;

// const data: Raw[] = [
//   {
//     month: 2,
//     row1: 1,
//     row2: 2,
//     row3: 3
//   },
//   {
//     month: 5,
//     row1: 4,
//     row2: 5,
//     row3: 6
//   },
//   {
//     month: 8,
//     row1: 7,
//     row2: 8,
//     row3: 9
//   }
// ];

// export const Table = ({ data }: { data: Raw[] }) => {
//   const formatted: Formatted = Object.fromEntries(
//     data.map(({ month, ...rows }) => [month, rows])
//   );

//   const rowKeys = Object.keys(Object.values(formatted)[0] || {});

//   const monthNos = [...new Array(12)].map((_, i) => i);

//   const getValue = (month: number, row: string): string => {
//     const rows = formatted[month];
//     return rows ? rows[row].toString() : "";
//   };

//   return (
//     <table>
//       <thead>
//         <tr>
//           <th></th>
//           {monthNos.map((i) => (
//             <th scope="col">Month #{i}</th>
//           ))}
//         </tr>
//       </thead>
//       <tbody>
//         {rowKeys.map((key) => (
//           <tr key={key}>
//             <th scope="row">{key}</th>
//             {monthNos.map((i) => (
//               <td>{getValue(i, key)}</td>
//             ))}
//           </tr>
//         ))}
//       </tbody>
//     </table>
//   );
// };


export default function App() {
  const {
    register,
    handleSubmit,
    formState: { errors }
  } = useForm<FormValues>(); 


  return (
    <div>
    <form
      onSubmit={handleSubmit((data) => {
        console.log(data);
        var user_params = {};

        if (data.date!="" && data.clientid=="" && data.tradeid=="") {
          console.log(data.date)

          axios({
            method: 'get',
            url: '/trades/date/' + data.date
          })
          .then(function (response) {
            const results = document.getElementById("results")
            const query_label = document.createElement("p")
            query_label.id = "query-label"
            query_label.textContent = "Failed GTT details for Date: " + data.date
            results?.appendChild(query_label);

            const p = document.createElement("p")
            p.id = "result-box"
            p.textContent = JSON.stringify(response.data)
            console.log(response)
            results?.appendChild(p);
          })
          .catch(function (error) {
            // console.log(error);
            const results = document.getElementById("results")
            const query_label = document.createElement("p")
            query_label.id = "query-error"
            query_label.textContent = "An error has occurred as below. Please contact IT."
            results?.appendChild(query_label);

            const error_text = document.createElement("p")
            error_text.id = "result-box"
            error_text.textContent = error
            results?.appendChild(error_text);
          })
        }
        else if (data.date=="" && data.tradeid!="" && data.clientid=="") {
          axios({
            method: 'get',
            url: '/trades/tradeid/' + data.tradeid
          })
          .then(function (response) {
            const results = document.getElementById("results")

            const query_label = document.createElement("p")
            query_label.id = "query-label"
            query_label.textContent = "Failed GTT details for TradeID: " + data.tradeid
            results?.appendChild(query_label);


            const p = document.createElement("p")
            p.id = "result-box"
            p.textContent = JSON.stringify(response.data)
            console.log(response)
            results?.appendChild(p);
          })
          .catch(function (error) {
            const results = document.getElementById("results")
            const query_label = document.createElement("p")
            query_label.id = "query-error"
            query_label.textContent = "An error has occurred as below. Please contact IT."
            results?.appendChild(query_label);

            const error_text = document.createElement("p")
            error_text.id = "result-box"
            error_text.textContent = error
            results?.appendChild(error_text);
          })
        }
        else if (data.date=="" && data.tradeid=="" && data.clientid!="") {
          user_params = {
            "tradeID": data.tradeid,
          };
          axios({
          method: 'get',
          url: '/trades/clientid/' + data.clientid
        })
          .then(function (response) {
            const results = document.getElementById("results")

            const query_label = document.createElement("p")
            query_label.id = "query-label"
            query_label.textContent = "Failed GTT details for ClientID: " + data.clientid
            results?.appendChild(query_label);

            const p = document.createElement("p")
            p.id = "result-box"
            p.textContent = JSON.stringify(response.data)
            console.log(response)
            results?.appendChild(p);
          })
          .catch(function (error) {
            const results = document.getElementById("results")
            const query_label = document.createElement("p")
            query_label.id = "query-error"
            query_label.textContent = "An error has occurred as below. Please contact IT."
            results?.appendChild(query_label);

            const error_text = document.createElement("p")
            error_text.id = "result-box"
            error_text.textContent = error
            results?.appendChild(error_text);
          })
        }
        else {
          const results = document.getElementById("results")
          const p = document.createElement("p")
          p.textContent = "Please enter data into ONE search field."
          results?.appendChild(p);
        }


      })}
    >
      <Headers
        renderCount={renderCount}
        description="Please enter data into only one of the search fields."
      />
      <label htmlFor="date">Query by Date:</label>
      <input
        {...register("date", { 
          maxLength: { value: 8, message: "Input date as YYYYMMDD." }
        })}
        id="date"
        placeholder="YYYYMMDD e.g. 20210607"
      />
      {errors.date && <p>{errors.date.message}</p>}

      <label htmlFor="tradeid">Query by TradeID:</label>
      <input
        id="tradeid"
        {...register("tradeid")}
        placeholder="e.g. P98ICK2EYR-10003"
      />

      <label htmlFor="clientid">Query by ClientID:</label>
      <input
        id="clientid"
        {...register("clientid")}
        placeholder="e.g. CLIENT-WXS14"
      />

      <input type="submit" />

    </form>
    <div>
      <div id="results"> 
      {/* <Table data={data} /> */}
      </div>
    </div>
    </div>
  );
}
